simplesect.para:
(Note: option::* identifiers are links that take you to their documentation.) #errorEXAMPLESHORTENEDFORREADABILITY.BETTEREXAMPLESAREINTHE.TAR.GZ!
#include<iostream>
#include"optionparser.h"

enumoptionIndex{UNKNOWN,HELP,PLUS};
constoption::Descriptorusage[]=
{
{UNKNOWN,0,"","",option::Arg::None,"USAGE:example[options]\n\n"
"Options:"},
{HELP,0,"","help",option::Arg::None,"--help\tPrintusageandexit."},
{PLUS,0,"p","plus",option::Arg::None,"--plus,-p\tIncrementcount."},
{UNKNOWN,0,"","",option::Arg::None,"\nExamples:\n"
"example--unknown----this_is_no_option\n"
"example-unk--plus-pppfile1file2\n"},
{0,0,0,0,0,0}
};

intmain(intargc,char*argv[])
{
argc-=(argc>0);argv+=(argc>0);//skipprogramnameargv[0]ifpresent
option::Statsstats(usage,argc,argv);
option::Optionoptions[stats.options_max],buffer[stats.buffer_max];
option::Parserparse(usage,argc,argv,options,buffer);

if(parse.error())
return1;

if(options[HELP]||argc==0){
option::printUsage(std::cout,usage);
return0;
}

std::cout<<"--pluscount:"<<
options[PLUS].count()<<"\n";

for(option::Option*opt=options[UNKNOWN];opt;opt=opt->next())
std::cout<<"Unknownoption:"<<opt->name<<"\n";

for(inti=0;i<parse.nonOptionsCount();++i)
std::cout<<"Non-option#"<<i<<":"<<parse.nonOption(i)<<"\n";
}
